  //this is the component for the table with the results
  function ResultWindow() {
    if (windState == "Null") {
      return (
        <table className="table-auto bg-[#ffffff] border-[#D0D0D0] rounded-sm m-3 border w-8/12 h-[100px] mb-10"></table>
      );
    }
    if (windState == "word") {
      return (
        <table className="table-auto bg-[#ffffff] border-[#D0D0D0] rounded-sm m-3 border w-8/12 mb-10">
          <thead>
            <tr>
              <td className="border-[#D0D0D0] border p-3 w-1/4">
                نسبة التطابق
              </td>
              <td className="border-[#D0D0D0] border p-3">السبب</td>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="border-[#D0D0D0] border p-3 w-1/4 font-bold">
                {result.score}
              </td>
              <td className="border-[#D0D0D0] border p-3 font-bold">
                {result.reasons}
              </td>
            </tr>
          </tbody>
        </table>
      );
    }
    if (windState == "scv") {
      return (
        <div className="w-full flex justify-center flex-col items-center">
          <div className="self-start pr-48 flex-row flex">
            <p className="font-extralight text-[#666666]">
              {" "}
              عدد الكلمات المتشابهة:{" "}
            </p>
            <p className="font-bold pr-1">{arrResult.length}</p>
            <p className="pr-1 font-light">كلمة </p>
          </div>
          <table className="table-auto bg-[#ffffff] border-[#D0D0D0] rounded-sm m-3 border w-8/12 mb-10">
            <thead>
              <tr>
                <td className="border-[#D0D0D0] border p-3 w-1/3 font-extralight">
                  النص الثانوي المطابق
                </td>
                <td className="border-[#D0D0D0] border p-3 font-extralight">
                  نسبة التطابق
                </td>
                <td className="border-[#D0D0D0] border p-3 font-extralight">
                  السبب
                </td>
              </tr>
            </thead>
            <tbody>
              {arrResult
                .slice((active - 1) * items, (active - 1) * items + items)
                .map((resultt) => (
                  <tr key="row">
                    <td
                      key={resultt.word}
                      className="border-[#D0D0D0] border p-3 font-bold"
                    >
                      {resultt.word}
                    </td>
                    <td
                      key={resultt.reasons}
                      className="border-[#D0D0D0] border p-3 font-bold"
                    >
                      {resultt.score}
                    </td>
                    <td
                      key={resultt.reasons}
                      className="border-[#D0D0D0] border p-3 font-bold"
                    >
                      {resultt.reasons}
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
          <div className="p-5">
            <SimplePagination />
          </div>
        </div>
      );
    }
  }

